import java.util.HashSet;
import java.util.Set;

public class Navigator {
    private Set<String> destinations = new HashSet<>();

    public void setDestination(String place) {
        if (isValidDestination(place))
            destinations.add(place);
    }

    public Set<String> getDestinations() {
        return destinations;
    }

    public String getDirection() {
        // Let's assume some logic here to calculate the best direction
        return "Go straight, then take a left";
    }

    private boolean isValidDestination(String place) {
        return place != null && !place.isEmpty();
    }
}
