        $(function () {
            var chat = $.connection.chatHub;

            chat.client.receiveMessage = function (user, message) {
                $('#messagesList').append('<li><strong>' + user + '</strong>: ' + message);
            };

            $('#sendButton').click(function () {
                chat.server.send($('#userInput').val(), $('#messageInput').val());
            });
        });
    </script>
</body>
